def resource_path(relative_path):
    """获取程序中所需文件资源的绝对路径"""
    try:
        # PyInstaller创建临时文件夹,将路径存储于_MEIPASS
        base_path = sys._MEIPASS
    except Exception:
        base_path = os.path.abspath(".")

    return os.path.join(base_path, relative_path)
def sort_category(x):
    data=pd.read_csv(resource_path("shenlun/score.csv"), encoding="gbk")
    dic={}
    for item in zip(data["category"],data["score_o"]):
        dic[item[0]]=item[1]
    if (int)(x)==1:
        dic=sorted(dic.items(),key=lambda x:x[1],reverse=True )
    else:
        dic = sorted(dic.items(), key=lambda x: x[1], reverse=False)
    # print([item[0] for item in dic])
    return [item[0] for item in dic]
