    def get_checked(self, node: QTreeWidgetItem,num=0)->list:
        """ 得到当前节点选中的所有分支， 返回一个 list """
        temp_list = []
        # 此处看下方注释 1
        for item in node.takeChildren():
            # 判断是否选中
            if item.checkState(0) == Qt.Checked or num==1:
                # if item.childCount()==0:
                temp_list.append(item.text(0))
                # 判断是否还有子分支
                if item.childCount():
                    temp_list.extend(self.get_checked(item,1))
            elif num==0:
                temp_list.extend(self.get_checked(item, 0))
            node.addChild(item)
        # print(temp_list)
        return temp_list
    def get_not_checked(self, node: QTreeWidgetItem,num=0)->list:
        """ 得到当前节点选中的所有分支， 返回一个 list """
        temp_list = []
        # 此处看下方注释 1
        for item in node.takeChildren():
            # 判断是否选中
            if num==1:
                return temp_list
            elif item.checkState(0) != Qt.Checked:
                # if item.childCount()==0:
                temp_list.append(item.text(0))
                # 判断是否还有子分支
                if item.childCount():
                    temp_list.extend(self.get_not_checked(item,0))
            else:
                temp_list.extend(self.get_not_checked(item, 1))
            node.addChild(item)
        # print(temp_list)
        return temp_list

